package org.springframework.samples.petclinic.model;


/**
 *
 *
 * @author Michael Isvy Simple test to make sure that Bean Validation is working (useful
 * when upgrading to a new version of Hibernate Validator/ Bean Validation)
 */
public class ValidatorTests {
    private javax.validation.Validator createValidator() {
        org.springframework.validation.beanvalidation.LocalValidatorFactoryBean localValidatorFactoryBean = eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.validation.beanvalidation.LocalValidatorFactoryBean>observeState("org.springframework.samples.petclinic.model.ValidatorTests|createValidator()|0", org.springframework.validation.beanvalidation.LocalValidatorFactoryBean.class, new org.springframework.validation.beanvalidation.LocalValidatorFactoryBean());
        eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.validation.beanvalidation.LocalValidatorFactoryBean>observeState("org.springframework.samples.petclinic.model.ValidatorTests|createValidator()|1", org.springframework.validation.beanvalidation.LocalValidatorFactoryBean.class, localValidatorFactoryBean).afterPropertiesSet();
        return eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.validation.beanvalidation.LocalValidatorFactoryBean>observeState("org.springframework.samples.petclinic.model.ValidatorTests|createValidator()|2", org.springframework.validation.beanvalidation.LocalValidatorFactoryBean.class, localValidatorFactoryBean);
    }

    @org.junit.Test
    public void shouldNotValidateWhenFirstNameEmpty() {
        org.springframework.context.i18n.LocaleContextHolder.setLocale(eu.stamp_project.reneri.instrumentation.StateObserver.<java.util.Locale>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|0", java.util.Locale.class, java.util.Locale.ENGLISH));
        org.springframework.samples.petclinic.model.Person person = eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.samples.petclinic.model.Person>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|1", org.springframework.samples.petclinic.model.Person.class, new org.springframework.samples.petclinic.model.Person());
        eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.samples.petclinic.model.Person>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|2", org.springframework.samples.petclinic.model.Person.class, person).setFirstName("");
        eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.samples.petclinic.model.Person>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|3", org.springframework.samples.petclinic.model.Person.class, person).setLastName("smith");
        javax.validation.Validator validator = eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.Validator>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|4", javax.validation.Validator.class, createValidator());
        java.util.Set<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>> constraintViolations = eu.stamp_project.reneri.instrumentation.StateObserver.<java.util.Set<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|7", java.util.Set.class, eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.Validator>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|5", javax.validation.Validator.class, validator).validate(eu.stamp_project.reneri.instrumentation.StateObserver.<org.springframework.samples.petclinic.model.Person>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|6", org.springframework.samples.petclinic.model.Person.class, person)));
        org.assertj.core.api.Assertions.assertThat(eu.stamp_project.reneri.instrumentation.StateObserver.observe("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|9", eu.stamp_project.reneri.instrumentation.StateObserver.<java.util.Set<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|8", java.util.Set.class, constraintViolations).size())).isEqualTo(1);
        javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person> violation = eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|12", javax.validation.ConstraintViolation.class, eu.stamp_project.reneri.instrumentation.StateObserver.<java.util.Iterator<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|11", java.util.Iterator.class, eu.stamp_project.reneri.instrumentation.StateObserver.<java.util.Set<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|10", java.util.Set.class, constraintViolations).iterator()).next());
        org.assertj.core.api.Assertions.assertThat(eu.stamp_project.reneri.instrumentation.StateObserver.observe("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|15", eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.Path>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|14", javax.validation.Path.class, eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|13", javax.validation.ConstraintViolation.class, violation).getPropertyPath()).toString())).isEqualTo("firstName");
        org.assertj.core.api.Assertions.assertThat(eu.stamp_project.reneri.instrumentation.StateObserver.observe("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|17", eu.stamp_project.reneri.instrumentation.StateObserver.<javax.validation.ConstraintViolation<org.springframework.samples.petclinic.model.Person>>observeState("org.springframework.samples.petclinic.model.ValidatorTests|shouldNotValidateWhenFirstNameEmpty()|16", javax.validation.ConstraintViolation.class, violation).getMessage())).isEqualTo("must not be empty");
    }
}

